import React, { Component } from 'react';
import { Button } from 'reactstrap';
import { Container, Row, Col } from 'reactstrap';
import './App.css';
import CheckboxTree from 'react-checkbox-tree';
import File from "./File"
import Folder from "./Folder"
let aes256 = require('aes256');

class App extends Component {
  state = {
    childrens: [],
    checked: [],
    expanded: [],
    nodes : [{
      value: 'mars',
      label: 'Home',
      ruta: "Home",
      children: [
        { 
        value: 'phobos', 
        label: 'Phobos' 
        },
        { 
         value: 'deimos', 
         label: 'Deimos' ,
         children: [
          { 
          value: 'phobos', 
          label: 'Phobos' 
          },
          { 
           value: 'deim', 
           label: 'Deim' ,
           children: [
            { 
            value: 'phobos', 
            label: 'Phobos' 
            },
            { 
             value: 'deimo', 
             label: 'Deimos' ,

            },
          ],

          },
        ],
        },
      ],
    }]
  };

  componentDidMount() {

    function searchInObj(obj, value, result) {

      // check for array and call for every item
      if (Array.isArray(obj)) {

        // primary flag for array.
        var r = false;
        obj.forEach(function(item, index) {

          // temporary flag for every iteration.
          var _r = searchInObj(item, value, result);
          if (_r) result.push(index)

          // if one of element returned true, array should return true.
          r = _r || r;
        });
        return r;
      } 

      // If Object, loop over properties
      else if (typeof(obj) === "object") {
        for (var k in obj) {

          // If object, check if property is Object/Array and call self.
          if (typeof(obj[k]) === "object") {
            var r = searchInObj(obj[k], value, result);
            if (r) result.push(k);
            return r;
          } 

          // If property is not Array/Object, match value
          else if (obj[k] === value) {
            result.push(k);
            return true;
          } 
          // If no match, return false
          else {
            return false;
          }
        }
      }
    }

    var data = this.state.nodes;

    var result = []
    searchInObj(data, "deimo", result);
    vonsole.log
    document.write("<pre>" + JSON.stringify(result.reverse(), 0, 4) + "</pre>");
  }

  clickInLoad = () => {
    document.getElementById("inputUpload").click();
    let UpdFile = document.getElementById("inputUpload");
    UpdFile.addEventListener('change',this.handleFileSelect, false);
  }

  callFile = async () => {
    const response = await fetch('/api/getFile');
    const body = await response.blob();
    if (response.status !== 200) throw Error(body.message);
    let key = "my passphrase;"
    let fileReader = new FileReader();

    fileReader.onloadend = function (e){

      let content = aes256.decrypt(key,fileReader.result);
      console.log("contentdes", content);
      var fileDown =[];
      var filename = "pruba.pdf";
      fileDown[0]=content;
      fileDown[1]=filename;

      fetch('api/postForDownload', { // Your POST endpoint
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({ "file": {
          "content" : content,
          "name" : filename
        }}) // This is your file object
        }).catch(function() {
          console.log("error in post data");
        });
     // download(content,filename,"js");

      function download(data, filename, type) {
        var file = new Blob([data], {type: type});
        if (window.navigator.msSaveOrOpenBlob) // IE10+
            window.navigator.msSaveOrOpenBlob(file, filename);
        else { // Others
            var a = document.createElement("a"),
                    url = URL.createObjectURL(file);
            a.href = url;
            a.download = filename;
            document.body.appendChild(a);
            a.click();
            setTimeout(function() {
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);  
            }, 0); 
        }

      }

    }
    fileReader.readAsText(body);
  };

  handleFileSelect = (evt) => {
    if(evt.target.files.length>0){
      var files = evt.target.files; // FileList object
      console.log("llamo")
      this.postContentFile(files);

    }
  }
  postContentFile = async (files) =>{
    let flagError=false;

      var formData = new FormData();
      //formData.set('files', files);
      console.log("entro", formData)
      formData.append('files', files[0]);
      fetch('api/postContentFile', { // Your POST endpoint
      method: 'POST',
      body: formData // This is your file object
      }).catch(function() {
        console.log("error in post data");
        flagError=true;
      });

    this.getContentFile(files,flagError);
  }

  getContentFile = async (files,flagError) =>{
    const response = await fetch('api/getContentFile?name='+files[0].name).catch(function() {
			console.log("error in get files");
			flagError=true;
			});
			if(!flagError){
			let json = await response.json();

				setTimeout(() => {
          var filename = files[0].name;
          console.log("json",json)

          var key = "my passphrase;"
          // var contentEncrypted = aes256.encrypt(key, contentString);
          var contentEncrypted = aes256.encrypt(key, json)
          var nameEncrypted = aes256.encrypt(key, filename);
          //nameEncrypted = nameEncrypted.toString().replace(":", "\/");
          //
          /* create file encrypt */
          var fileEncrypted = new File([contentEncrypted], nameEncrypted);
          console.log("fileEncrypted",fileEncrypted);
          var formData = new FormData();
          //formData.set('files', files);
          formData.append('files', fileEncrypted);
          fetch('api/file', { // Your POST endpoint
            method: 'POST',
            body: formData // This is your file object
          })

				}, 500);
			}
  }

  selected = (clicked) => {
    console.log(clicked)
    if(clicked.isParent){
      console.log(clicked.children)
      this.setState({childrens:clicked.children});
    }else{
      console.log("hoja")
    }
  }

render() {
  const children = [];
    let auxChildren=this.state.childrens;
    for (var i = 0; i < auxChildren.length; i += 1) {

      if(typeof auxChildren[i].children !== "undefined") {  // ES PADRE (Carpeta)
        children.push(<Col className="nodeCard" xs="2"><Folder key={i} number={i} labelFile={auxChildren[i].label} valueFile={auxChildren[i].label} /></Col>);
      }else{//Es un file
        children.push(<Col className="nodeCard" xs="2"><File key={i} number={i} labelFile={auxChildren[i].label} valueFile={auxChildren[i].label}/></Col>);
      }

    };
    return (
      <Container className="App">
        <Row>
          <Button onClick={this.callFile} color="primary">Create Folder</Button>{' '}
        </Row>
        <br></br>
        <br></br>

        <Row> 
          <Col xs="4">
            <CheckboxTree
              nodes={this.state.nodes}
              checked={this.state.checked}
              expanded={this.state.expanded}
              onCheck={checked => this.setState({ checked })}
              onExpand={expanded => this.setState({ expanded })}
              onClick={clicked => this.selected(clicked)}
            />
          </Col>
          <Col xs="8">
            <Row>
            {children}
            </Row>
          </Col>
        </Row>

        {/* file */}

        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
