        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <Button onClick={this.clickInLoad} color="primary">primary</Button>{' '}
        <input type="file" id="inputUpload" name="files[]" multiple/> 

        <Button onClick={this.callFile} color="primary">call file</Button>{' '}


      </Container>
    );
  }
}

